"use strict";function Cart(){}Cart.prototype.getCar=function(){return JSON.parse(localStorage.getItem("cartlist"))||[]},Cart.prototype.addCar=function(t){var r=JSON.parse(localStorage.getItem("cartlist"))||[];if(this.hasGoods(t.id))for(var e=0;e<r.length;e++)r[e].id==t.id&&(r[e].number+=t.number);else r.push(t);localStorage.setItem("cartlist",JSON.stringify(r))},Cart.prototype.hasGoods=function(t){for(var r=JSON.parse(localStorage.getItem("cartlist"))||[],e=0;e<r.length;e++)if(r[e].id==t)return!0;return!1},Cart.prototype.delCarGoods=function(t){for(var r=JSON.parse(localStorage.getItem("cartlist"))||[],e=0;e<r.length;e++)if(t==r[e].id)return r.splice(e,1),localStorage.setItem("cartlist",JSON.stringify(r)),!0;return!1},Cart.prototype.getTotalPrice=function(){for(var t=this.getCar(),r=0,e=0;e<t.length;e++)r+=t[e].price*t[e].number;return r};